export default class ContactMeSection extends Component {

    // const classes = useStyles();


    constructor(props) {
        super(props);

      this.onChangeFullName = this.onChangeEmail.bind(this);
      this.onChangeSubject = this.onChangeSubject.bind(this);
      this.onChangeEmail = this.onChangeEmail.bind(this);
      this.onChangeMessage = this.onChangeMessage.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state ={
          fullName:'',
          subject:'',
          email:'',
          message:'',
      };
    }

    componentDidMount() {
        this.setState({
            fullName:'test user'
        })
    }

    onChangeFullName(e){
        this.setState({
            fullName: e.target.value
        });
    }

    onChangeSubject(e){
        this.setState({
            subject: e.target.value
        });
    }

    onChangeEmail(e){
        this.setState({
            email: e.target.value
        });
    }

    onChangeMessage(e){
        this.setState({
            message: e.target.value
        });
    }

    onSubmit(e){
        e.preventDefault();

        if(this.state.fullName === "")
        {
            alert("Need a name to submit!")
            return;
        }
        if(this.state.email === "")
        {
            alert("Need an email to submit!")
            return;
        }
        if(this.state.message ==="")
        {
            alert("Please add a message before submitting!")
            return;
        }

        let formToSubmit = {
            fullName: this.state.fullName,
            subject: this.state.subject,
            email: this.state.email,
            message: this.state.message
        }

        console.log(formToSubmit);

        alert("Submitting message!")
        axios.post('http://localhost:5000/add', formToSubmit)
            .then(res => console.log(res.data))
            .catch(err => console.log("Error: " + err))
    }


