import React, {useState} from 'react';
import './ContactMeSection.css';
import {Button, Container, Grid, TextField} from "@material-ui/core";
import axios from "axios";
// import {makeStyles} from "@material-ui/styles";

// const useStyles = makeStyles((theme) => ({
//     root: {
//         '& > *': {
//             margin: theme.spacing(1),
//             width: '25ch',
//         },
//     },
// }));




const ContactMeSection = () =>{

    // const classes = useStyles();
    const[fullName, setFullName] = useState("");
    const[subject, setSubject] = useState("");
    const[email, setEmail] = useState("");
    const[message, setMessage] = useState("");

    function handleFormSubmit(){
        if(fullName === "")
        {
            alert("Please provide a name!");
            return;
        }
        if(email === ""){
            alert("Please provide an email!");
            return;
        }
        if(message === "")
        {
            alert("Please provide a message!");
            return
        }


        axios
            .post("/api/formSubmission", {
                fullName : fullName,
                email : email,
                subject : subject,
                message : message,
            })
            .then(function(){
                alert("Message submitted, will contact you soon!");
                window.location.reload();
            })
            .catch(function ()
            {
                alert("Could not upload message, please try again");
            })
    }


    return(
        <div className='contact-container' id="ContactMeSection">
            <Container className="contact-me-form-container"  fixed>
                    <Grid
                        container
                        direction="column"
                        justify="center"
                        alignItems="stretch">
                        <h1 className='contact-me-header'>CONTACT ME</h1>
                        <div className="contact-me-underline"/>
                        <div className='contact-me-text-container'>
                            <p>
                                I am always looking for new and interesting projects to collaborate on. For any inquiries or to view my resume
                                please email me at my email below and I'll get back to you!
                            </p>
                        </div>
                        <form noValidate autoComplete="on">
                            <TextField className="textfield-component" id="standard-basic" label="Full Name" color="secondary" margin="normal" variant="filled" InputProps={{className:"textfield__body"}} InputLabelProps={{className: "textfield__label"}} onChange={(e) => setFullName(e.target.value)}/>
                            <TextField className="textfield-component" id="standard-basic" label="Email"  color="secondary" margin="normal" variant="filled" InputProps={{className:"textfield__body"}} InputLabelProps={{className: "textfield__label"}} onChange={(e) => setSubject(e.target.value)}/>
                            <TextField className="textfield-component" id="standard-basic" label="Subject of Message"  color="secondary" margin="normal" variant="filled" InputProps={{className:"textfield__body"}} InputLabelProps={{className: "textfield__label"}} onChange={(e) => setEmail(e.target.value)}/>
                        </form>
                        <form noValidate autoComplete="off">
                            <TextField
                                className="textfield-component"
                                id="outlined-multiline-static"
                                label="Message"
                                color="secondary"
                                multiline
                                rows={4}
                                defaultValue="Type Message Here..."
                                variant="outlined"
                                margin="normal"
                                InputProps={{className:"textfield-message-body"}} InputLabelProps={{className: "textfield__label"}}
                                onChange={(e) => setMessage(e.target.value)}
                            />
                        </form>
                        <Button className="submit-button" variant="outlined" color="secondary" onClick={handleFormSubmit}>Submit</Button>
                    </Grid>
            </Container>
        </div>
    );
}

export default ContactMeSection;
