import React, {useState} from "react";
import './Navbar.css';
import AboutMeSection from "../AboutMeSection";
import IntroSection from "../IntroSection";
import ContactMeSection from "../ContactMeSection";
import {HashLink} from "react-router-hash-link";

function Navbar (){
    /* TODO:
    *   -Have a function that keeps the underline on the section that is selected
    *
    *
    * */


    const[click, setClick] = useState(false);

    const handleMenuClick = () => setClick(!click);

    const closeMobileMenu = () => setClick(false);

    return(
        <>
        <nav className='navbar'>
            <div className='navbar-container'>
                <HashLink to='/' className='navbar-logo' onClick={closeMobileMenu}>
                    JSII
                </HashLink>
            </div>
            <div className='menu-icon' onClick={handleMenuClick}>
                <i className={click ? "fas fa-minus" : "fas fa-ellipsis-v"}/>
            </div>
            <ul className={click ? "nav-menu active" : "nav-menu"}>
                    <li className="nav-item">
                        <HashLink to='/#AboutMeSection' className='nav-links' onClick={closeMobileMenu}>
                            About Me
                        </HashLink>
                    </li>
                    <li className="nav-item">
                        <HashLink to='/#ContactMeSection' className='nav-links' onClick={closeMobileMenu}>
                            Contact Me
                        </HashLink>
                    </li>
            </ul>
        </nav>
            <IntroSection/>
            <AboutMeSection/>
            <ContactMeSection/>
        </>
    );
}


export default Navbar;
